import React, { Component } from 'react';
import TaskList from './task-list';
import Header from './header';
import './App.css';

class App extends Component {

  constructor(props){
    super(props);

    this.state = {
      task:'',
      arrtaskList: []
  }

    this.addNewTask = this.addNewTask.bind(this);
  }

   //function to add the task on an array in state
  addNewTask(newTask) {
    const arrtaskListNew = this.state.arrtaskList.slice();
    arrtaskListNew.push(newTask);
    this.setState({task: arrtaskListNew});
  }


  render() {
    return (
      <div className="App">
        <Header addTask={this.addNewTask}/>
        <TaskList />
        <h1>{this.state.task}</h1>
      </div>
    );
  }
}

export default App;
